data class FeedModel(
        val merge_type: String? = null,
        val merged: MergedModel? = null,
        val main_action: String? = null,
        val action: String,
        val create_time: Int = 0,
        val user: UserModel? = null,
        val publish_time_human: String? = null,
        val update_time_human: String? = null,
        val publish_time_before: String? = null,
        val article: ArticleModel? = null,
        val voter_count: Int = 0,
        val action_text: String? = null,
        val review: ReviewModel? = null,
        val game: GameModel? = null,
        val answer: AnswerModel? = null,
        val sharelink: ShareLinkModel? = null,
        val comment: CommentModel? = null,
        val question: QuestionModel? = null,
        val has_more: Int = 0,
        val voters: List<VotersModel>? = null,
        val users: List<UserModel>? = null
) : BaseModel() {
    enum class Type {
        type_unknow,
        editor_elite_answer,        // 回答了问题
        editor_elite_review,        // 评价了游戏
        system_recommend_user,
        editor_elite_article,       // 发布了文章
        post_submit_review,
        tag_answer,
        vote_answer,
        vote_article,
        tag_article,
        tag_question,               // 提交了问题
        tag_sharelink,
        post_submit_answer,         // 回答了问题
        follow_question,
        sharelink_comment,
        sharelink_add,              // 分享了链接
        vote_comment,
        submit_question,
        post,
        post_submit_question,
        post_submit_sharelink,      // 分享了链接
        post_submit_article;        // 发布了文章

        fun getName(): String {
            return name
        }
    }

    override fun getType(): ItemTypeEnum {
        return ItemTypeEnum.TYPE_FEED
    }
}
